mapper.insert:
INSERT INTO comments(
	postSeq,groupNo,commentSeq,userId,content
)
VALUES(
	#{postSeq}, nextval('commentSeq'), currval('commentSeq'), #{userId}, #{content}
)

mapper.update:
UPDATE comments
SET content = #{content}
WHERE commentSeq = #{commentSeq}

UPDATE comments
SET status = 0
WHERE commentSeq = #{commentSeq}
