mapper.insert:
INSERT INTO posts(
	boardUrl,groupNo,seq,userId,title,content
)
VALUES(
	#{boardUrl}, nextval('postseq'), currval('postseq'), #{userId}, #{title}, #{content}
)

mapper.select:
SELECT userId
FROM posts
WHERE seq = #{postSeq}

mapper.update:
UPDATE posts
SET title = #{title},
	content = #{content}
WHERE boardUrl = #{boardUrl}
AND seq = #{seq}

UPDATE posts
SET status = 0
WHERE seq = #{seq}
